sp_widget.client_script:
api.controller = function($scope, $http, spUtil) {
  var c = this;
  c.form = {};

  c.login = function() {
    if (!c.form.email || !c.form.password) {
      spUtil.addErrorMessage("Please enter both email and password.");
      return;
    }

    var query = "email=" + encodeURIComponent(c.form.email) +
                "^password=" + encodeURIComponent(c.form.password);

    $http.get("/api/now/table/x_1606464_juanid_juanid_user?sysparm_query=" + query)
      .then(function success(response) {
        if (response.data.result.length === 1) {
          const user = response.data.result[0];
          sessionStorage.setItem("juanid_user_id", user.sys_id);

          if (!user.userdata) {
            window.location.href = "?id=upload";
          } else {
            window.location.href = "?id=home"; // or dashboard
          }
        } else {
          spUtil.addErrorMessage("Invalid credentials.");
        }
      }, function error(err) {
        spUtil.addErrorMessage("Login failed: " + (err.data.error.message || err.statusText));
      });
  };
};
